mapper.update:

update  schedule
set     ptNo = #{ptNo}, state = #{state}, memo = '', startTime = to_date(#{startTime}, 'yyyy-mm-dd hh24:mi'), endTime = to_date(#{startTime}, 'yyyy-mm-dd hh24:mi') + #{amount}/(24*60)
where   scheduleNo = #{scheduleNo}


