mapper.select:


SELECT count(userNo)
FROM users
WHERE userId = #{id}

mapper.update:


UPDATE users
SET birthDate = #{birthDate},
    career = #{career},
    location = #{location},
    company = #{company},
	introduction = #{introduction},
    price = #{price}




    ,profileImg = #{profileImg}





	,name = #{name}





	,phone = #{phone}





	,gender = #{gender}





	,workingHours = #{workingHours}






	,invisible = ''




	,invisible = 'invisible'





WHERE userNo = #{userNo}

mapper.select:


SELECT userNo, userId, name, userType
FROM users
WHERE userId = #{userId}
AND password = #{password}



SELECT name, gender, phone, TO_CHAR(birthDate, 'yyyy/mm/dd') "birthDate", introduction, career, location, company, price, profileImg, invisible, workingHours
FROM users
where userNo = #{userNo}



SELECT careerNo, recordInfo
FROM career_list
WHERE userNo = #{userNo}
